	@RequestMapping( value="/like",
			method = {RequestMethod.POST, RequestMethod.GET},
			produces = MediaType.APPLICATION_JSON_VALUE)
    @ResponseStatus(HttpStatus.OK)
	@ResponseBody
	public HashMap<String, Object> findAllProjectPaging(
	@RequestHeader(value="token",required=false) String token,
	@RequestBody(required = true) String json
	) throws ParseException {
		UserItem user =AuthorizationToken.convertToObject(token);
		HashMap<String, Object> ret =new HashMap<String, Object>();
		try {
			JSONParser parser = new JSONParser();
			JSONObject jsonObj = (JSONObject) parser.parse(json);
			Integer page = DataUtils.parseInt(jsonObj.get("page"));
			List<Content> list_like= contentService.findContentLike(user.getId(), page, 12);
			Integer total_count = contentService.totalCountContentLike(user.getId());
			Double total_page = Math.ceil(Float.valueOf(total_count)/Float.valueOf("12"));
			ret.put("list", list_like);
			ret.put("page", page);
			ret.put("totalPage", total_page);
			return SUCCESS(ret);
			
		} catch (Exception e) {
			return FAIL("HAVE ERROR: "+e);
		}
		
	} 


	@RequestMapping( value="/item/{id}",
			method = {RequestMethod.POST, RequestMethod.GET},
			produces = MediaType.APPLICATION_JSON_VALUE)
    @ResponseStatus(HttpStatus.OK)
	@ResponseBody
	public HashMap<String, Object> findOne(
	@PathVariable("id") Integer id,
	@RequestHeader(value="token") String token
		) {
		logger.info("");
		UserItem user =AuthorizationToken.convertToObject(token);
		try {
			Content item= contentService.findById(id);
			LikesEntity liked = likesService.findByUserIdAndThreadIdAndType(user.getId(), item.getId(), 2);
			if(liked != null){
				item.setIsLike(1);
			}
			if(item!=null){
				categoryService.updateCategoryCount(item.getCategoryId());
				return SUCCESS(item);
			}else{
				return SUCCESS();
			}
		} catch (Exception e) {
			return FAIL("HAVE ERROR: "+e);
		}
		
	}
	
