	@RequestMapping(value = "/create", method = RequestMethod.POST ) 
	public String create(
			HttpSession session,
			@ModelAttribute Event event,
			@RequestParam(value="file") MultipartFile file,
			Model model) {
		log("Action 'create'");
		try {
			if(file.getSize() > 0){
				String path = FileUtils.saveFileOrigin(file,"upload", servletContext);
				event.setImageUrl(path);
				event.setImageName(file.getOriginalFilename());
			}
			event.setViewCount(0);
			event.setCreateDate( DataUtils.getNowDate() );
			event.setUpdateDate( DataUtils.getNowDate() );
			eventService.create(event);
			return "redirect:/"+JSP_LIST;
		} catch(Exception e) {
			System.out.println(e);
			return JSP_CREATE;
		}
	}

