	@RequestMapping(value = "/edit/{id}", method = RequestMethod.POST)
	public String update(
	@RequestParam(value="edit",defaultValue="0") Integer edit,
	@ModelAttribute Event item,
	@RequestParam(value="file") MultipartFile file,
	HttpSession session,
	@PathVariable("id") Integer id,
	Model model  ) {
		Event event = eventService.findById(id);
		try {
			if(file.getSize() > 0){
				String path = FileUtils.saveFileOrigin(file,"upload", servletContext);
				event.setImageUrl(path);
				event.setImageName(file.getOriginalFilename());
			}
			event.setTitle( item.getTitle() );
			event.setContent( item.getContent() );
			event.setUpdateDate( DataUtils.getNowDate() );
			event = eventService.update(event);
			model.addAttribute("item", event);
		} catch(Exception e) {
			System.out.println(e);
		}
		return "redirect:/"+JSP_EDIT+"/"+id;
	}
	
	

